/* BOTONES */
/* Botones rojos */
.redButton, .redButtonBig, .redButtonSmall{
    cursor: pointer;
    background: #EC0000; 
    border: 1px solid #EC0000; 
    transition: all 0.2s ease-in-out;
    color: #ffffff;
}
.redButton:hover, .redButtonBig:hover, .redButtonSmall:hover{ 
    background: #CC0000; 
    border: 1px solid #CC0000;    
    color: #ffffff;
}

.redButton:focus, .redButtonBig:focus, .redButtonSmall:focus{ 
    background: #CC0000; 
    border: 1px solid #CC0000;    
    color: #ffffff;
    box-shadow: 2px 2px 4px 2px rgba(206,214,219,0.64);   
}

.redButton[disabled], .redButtonBig[disabled], .redButtonSmall[disabled]{
    cursor: not-allowed;
    background: #CCC; 
    border: 1px solid #CCC;
    color: #ffffff;
}
/* Botones Blanco */
.whiteButton, .whiteButtonBig1, .whiteButtonBig2, .whiteButtonSmall{
    cursor: pointer;
    background: #FFF; 
    border: 1px solid #CC0000;
    color: #CC0000;
    transition: all 0.2s ease-in-out;
}
.whiteButton:hover, .whiteButtonBig1:hover, .whiteButtonBig2:hover, .whiteButtonSmall:hover{ 
    background: #F6F6F6; 
    border: 1px solid #CC0000;
    color: #CC0000; 
}
.whiteButton:focus, .whiteButtonBig1:focus, .whiteButtonBig2:focus, .whiteButtonSmall:focus{ 
    box-shadow: 2px 2px 4px 2px rgba(206,214,219,0.64);
    background: #F6F6F6; 
    border: 1px solid #CC0000;
    color: #CC0000;
}

.whiteButton[disabled], .whiteButtonBig1[disabled], .whiteButtonBig2[disabled], .whiteButtonSmall[disabled]{
    color: #cccccc;
    cursor: not-allowed;
    background: #FFF; 
    border: 1px solid #CCC;  
}

/* RADIO BUTTON */
.slds-radio [type=radio]+.slds-radio__label .slds-radio_faux {
    background-color: #ffffff;
    border: solid 1px #767676;
    border-radius: 50%;
    box-shadow: none;
    color: #ececec;
    cursor: pointer;
    height: 20px;
    margin-right: 10px;
    position: relative;
    vertical-align: middle;
    width: 20px;
    transition: all 0.2s ease-out;
}
.slds-radio [type=radio]:checked+.slds-radio__label span:first-child {
    border: solid 1px #cc0000;
}
.slds-radio [type=radio]:checked+.slds-radio__label .slds-radio_faux:after {
    background: #ec0000;
    border-radius: 50%;
    box-shadow: none;
    content: '';
    height: 10px;
    left: 50%;
    position: absolute;
    top: 50%;
    transform: translated(-50%, -50%, 0);
    width: 10px;
}
.slds-radio [type=radio]+.slds-radio__label .slds-radio_faux:hover{
    box-shadow: 2px 2px 4px rgba(0,0,0,0.15)!important;
}

/* ICONOS */
.slds-button_icon:before, [class^="icon-"]:before, [class*=" icon-"]:before{
    transition: all 0.2s ease-in-out;
    color: #EC0000; 
}
.slds-button_icon:hover:before, [class^="icon-"]:hover:before, [class*=" icon-"]:hover:before{
    color: #CC0000;
}
.slds-button_icon:focus:before, [class^="icon-"]:focus:before, [class*=" icon-"]:focus:before{
  color: #CC0000; 
  text-shadow: 0 0 4px 2px rgba(0,0,0,0.3);
}
.slds-button_icon[disabled]:before, [class^="icon-"][disabled]:before, [class*=" icon-"][disabled]:before{
   color: #CCC;
}

/* TEXT LINK */
.text-link {
    color: #EC0000;
    font-family: "SantanderMicroText";
    transition: all 0.2s ease-in-out;
}
.text-link:hover {
    color: #CC0000;
}
.text-link:focus {
  color: #CC0000; 
}
.text-link[disabled] {
   color: #CCC;
}

/* TOOLTIPS */
*[data-title]:after, 
*[data-title-left]:after,
*[data-title-right]:after,
*[data-title-on-center]:after,
*[data-title-on-right]:after,
*[data-title-on-left]:after,
*[data-title-top-center]:after,
*[data-title-top-right]:after,
*[data-title-top-left]:after {
	-moz-border-radius: 4px;
	-moz-box-shadow: 1px 1px 2px 0 rgba(68, 68, 68, 0.16), 1px 1px 4px 0 rgba(0, 0, 0, 0.15);
	-webkit-border-radius: 4px;
	-webkit-box-shadow: 1px 1px 2px 0 rgba(68, 68, 68, 0.16), 1px 1px 4px 0 rgba(0, 0, 0, 0.15);
	background-color: #ffffff!important;
	border-radius: 5px;
	bottom: -1.8em;
	box-shadow: 1px 1px 2px 0 rgba(68, 68, 68, 0.16), 1px 1px 4px 0 rgba(0, 0, 0, 0.15)!important;
	color: #444444;
	content: attr(data-title);
	font-family: "SantanderTextW05-Regular";
	font-size: 12px;
	height: 24px!important;
	left: 0;
    line-height: 1.2;
	opacity: 0;
	padding: 5px 8px 5px 8px;
	position: absolute;
	right: auto;
	top: auto;
	visibility: hidden;
	white-space: nowrap;
	width: auto;
	z-index: 99999;
}
*[data-title-left]:after {
	bottom: 0em;
	content: attr(data-title-left);
	left: auto;
	right: 100%;
	top: auto;
}
*[data-title-right]:after {
	bottom: 0em;
	content: attr(data-title-right);
	left: 100%;
	right: auto;
	top: auto;
}
*[data-title-top-center]:after {
	bottom: auto;
	content: attr(data-title-top-center);
	left: 50%;
	right: auto;
	top: -1.8em;
	transform: translateX(-50%);
}
*[data-title-top-left]:after {
	bottom: auto;
	content: attr(data-title-top-left);
	left: 0;
	right: auto;
	top: -1.6em;
}
*[data-title-top-right]:after {
	bottom: auto;
	content: attr(data-title-top-right);
	left: auto;
	right: 0;
	top: -1.6em;
}
*[data-title-on-center]:after {
	bottom: 0;
	content: attr(data-title-on-center);
	left: 50%;
	right: auto;
	top: 50%;
	transform: translateX(-50%)  translateY(-50%);
}
*[data-title-on-left]:after {
	bottom: 0;
	content: attr(data-title-on-left);
	left: 0;
	right: auto;
	top: 50%;
	transform: translateY(-50%);
}
*[data-title-on-right]:after {
	bottom: 0;
	content: attr(data-title-on-right);
	left: auto;
	right: 0;
	top: 50%;
	transform: translateY(-50%);
}
