*[data-title-on-left]:hover:after,
*[data-title-top-center]:hover:after,
*[data-title-top-right]:hover:after,
*[data-title-top-left]:hover:after {
	opacity: 1;
	transition: all 0.1s ease 0.5s;
	visibility: visible;
}

/* SPINNER */
.slds-spinner:after, .slds-spinner:before, .slds-spinner__dot-a:after, .slds-spinner__dot-a:before, .slds-spinner__dot-b:after, .slds-spinner__dot-b:before{
   background: #ec0000 !important; 
}

/* Modal */
.slds-modal{
    z-index:100000 !important;
}
.slds-backdrop{
    z-index:99000 !important;
}

/* Header */
.siteforcePrmBody .cHeader{
     z-index: 9999999;
    position: relative;
}
.spinnerclass{
     z-index: 9;
}

.clr-red {
    color: rgb(194, 57, 52);
}

/* Screen print */
@media print {
    @page {
       size:auto;
        margin: 0.5cm 0 ;   
    } 
    body{
        margin:0.5cm 1cm;
    }
    html { 
        margin:0;   
    }  
    .cHeader, .cFooter.footer{
        display:none!important;
    }
    .cCenterPanel {
        padding-top: 0!important;
    }
}

/* BUTTONS */
.slds-button{
    display: inline-flex;
    justify-content: center;
}



.secondary {
    background-color: #ffffff;
    border: 1px solid #ec0000;
    border-radius: 100px;
    color: #ec0000;
    font-family: "SantanderMicroText";
    font-size: 16px;
    line-height: 24px;
    padding: 8px 16px;
    cursor: pointer;
}

.primary {
    background-color: #ec0000;
    border-radius: 100px;
    color: #ffffff;
    cursor: pointer;
    font-family: "SantanderMicroText";
    font-size: 16px;
    line-height: 24px;
    padding: 8px 16px;
}

.primary.button_full,
.secondary.button_full{
    min-width: 184px;
}

.primary.button_small,
.secondary.button_small{
    padding: 4px 16px;
}

.primary.button_disabled,
.secondary.button_disabled {
    background-color: #ffffff;
    border: 1px solid #cccccc;
    color: #767676;
}

