/*HEADER*/
.siteforceServiceBody .cHeaderOverlay{
    background-color: #444444 !important;    
}
.siteforceServiceBody .cHeaderLine{
    width: 1184px !important;
    padding: 0rem 0rem 2.27rem 0rem !important;
    max-width: 1184px !important;
}

.siteforceServiceBody .cBrandingLogo{
    width: 100% !important;
}

.siteforceServiceBody .cSearchPublisher .forceCommunityGlobalSearchInput .forceSearchInputDesktop{
    margin-top: .43rem !important;
}

/*MENU*/
.forceCommunityGlobalNavigation {
    background-color: #ffffff !important;
    color: #444444 !important;
    box-shadow: 0 1px 2px 0 rgba(68, 68, 68, 0.24);
    min-height: 36px !important;
}
.slds-icon-utility-home{
    content:url('/s/sfsites/c/resource/Community_Home') !important;
    color: #444444 !important;
    height: 20px !important;
    width: 20px !important;
}

.slds-p-horizontal--x-small{
    padding: 0 !important;
}


/* HEADER - TABS */
.forceCommunityGlobalNavigation{
    background-color: #ffffff !important;
    box-shadow: 0 2px 4px 0 rgba(206, 214, 219, 0.4);
    color: #444444 !important;
}

.forceCommunityGlobalNavigation>*{
    max-width: 1186px !important;
}

/*.forceCommunityGlobalNavigation .slds-list_horizontal>.slds-list__item>.comm-navigation__item>button.slds-is-active:not([data-type="home"]){
background-color: rgba(236, 0, 0, 0.04) !important;
box-shadow: inset 0 -1px 0 0 #ec0000;
}
*/

.forceCommunityGlobalNavigation .slds-list_horizontal>.slds-list__item>.comm-navigation__item>.slds-is-active{
    background-color: rgba(236, 0, 0, 0.04) !important;
    box-shadow: inset 0 -1px 0 0 #ec0000;
}

.slds-icon_xx-small{
    width: 0.6rem;
    height: 0.6rem;
}

.slds-button__icon--right{
    margin-left: 12px !important;
}

.lightning-primitive-icon{
    align-items: center;
    display: flex;
}

.slds-p-horizontal_x-small, .slds-p-horizontal--x-small{
    padding: 0;
}

a.slds-container_fluid.slds-truncate.slds-is-active{
    background-color: rgba(236, 0, 0, 0.04);
    box-shadow: inset 0 -1px 0 0 #ec0000;
}

ul.slds-list_vertical.slds-is-nested.slds-is-absolute a.slds-container_fluid.slds-truncate.slds-is-active{
    box-shadow:none !important;
    padding: 6px 14px !important;
    font-size: 12px;
}
ul.slds-list_vertical.slds-is-nested.slds-is-absolute a.slds-container_fluid.slds-truncate{
    padding: 6px 14px !important;
    font-size: 12px;
} 

/*a.slds-container_fluid.slds-truncate{
font-size: 12px !important;
}*/

a:hover.slds-container_fluid.slds-truncate, button:hover.slds-container_fluid.slds-truncate{
    background-color: #f6f6f6 !important;
}

ul.slds-list_vertical.slds-is-nested.slds-is-absolute{
    margin-top: 4px;
    padding: 8px 0;
}

.siteforcePrmBody .comm-navigation .comm-navigation__item a {
    padding: 8px 36px 8px 16px !important;
}

.siteforcePrmBody .comm-navigation{
    min-height: 36px !important;
}

.siteforcePrmBody .comm-navigation .comm-navigation__item button, .siteforcePrmBody .comm-navigation .comm-navigation__item a {
    padding: 8.4px 16px 8.4px 16px !important;
    font-family: SantanderMicroText;
}


.slds-button:focus{
    box-shadow: none;
}

.forceCommunityGlobalNavigation .slds-list_vertical{
    background-color: #ffffff !important;
}


button.slds-container_fluid.slds-button.slds-button--reset.slds-truncate:active{
    box-shadow: inset 0 -1px 0 0 #ec0000;
}

.forceCommunityGlobalNavigation .slds-list__item a{
    padding: .375rem .75rem !important;
}

/*RECORD HEADER*/
.forceRecordLayout .primaryFieldRow .actionsContainer{
    display: none !important;
}

/* CASE LIST */
button.slds-button.slds-button_icon.slds-button_icon-more{
    display: none;
}
