/* CONTAINER */
.slds-col_padded, .slds-col--padded {
    padding: 0 !important;
}
.siteforceContentArea .comm-layout-column .ui-widget:not(:last-child), .siteforceContentArea .cb-section_column .ui-widget:not(:last-child){
    margin-bottom: 0 !important;
}

/* HEADER */
.forceCommunityThemeHeaderCompact .themeHeaderInner{
    padding-bottom: 0px !important;
    padding-top: 0px !important;
}

/* HEADER - Tabs */
.forceCommunityThemeNav .comm-navigation__top-level-item.mainNavItem:first-child{
    display: none;
}

.forceCommunityThemeHeaderCompact .themeUtils {
    padding-left: 24px !important;
}

.forceCommunityThemeHeaderCompact .themeUtils .themeUtil{
    margin-left: 0px !important;
}

nav.stellaInnerHeader.commThemeCmp.comm-navigation.themeNavContainer.navMenuType-expanded.alignPanel-left.forceCommunityThemeNav.doneRendering{
    color: #ffffff;
    letter-spacing: normal;
    line-height: 1.71;
    font-size: 14px;
    font-stretch: normal;
    font-family: SantanderTextW05-Bold;
}

li.mainNavItem.comm-navigation__top-level-item.comm-navigation__menu-item a{
    align-items: center; 
    display: flex;
    height: 48px;
    padding-left: 16px !important;
    padding-right: 16px !important;
}

/* HEADER - Profile */
.forceCommunityThemeProfileMenu .profile-icon{
    border-radius: initial !important;
    content:url('/sfsites/c/resource/Community_Profile') !important;
    height: 32px !important;
    width: 32px !important;
}

.forceCommunityThemeProfileMenu .profile-menuTrigger{
    height: 32px !important;
}
